import { useEffect, useState } from 'react';
import { useFormik, Formik, Form, Field, ErrorMessage } from 'formik';
import "./Login.css"
import { basicSchema } from '../../schemas';
import IconPaciente from '../IconPaciente';

const onSubmit = async (values, actions) => {
    console.log("values");
    console.log("actions");
    await new Promise((resolve) => setTimeout(resolve, 1000));
    actions.resetForm()
};

function Login(){
    const {values, errors,touched,isSubmitting, handleBlur, handleChange, handleSubmit} = useFormik({
      initialValues:{
        userEmail: '',
        userPassword: '',
    },   
    validationSchema:basicSchema,  
    onSubmit,
 });

console.log(errors);

 return(  
        <div className='cadastro'>

         <IconPaciente />
        <form onSubmit={handleSubmit} autoComplete="off" className="form" >

            <div className="formField">
                <label htmlFor="userEmail">Seu email</label>
                <input 
                id="userEmail"
                type="email" 
                placeholder="Digite seu email..." 
                onChange={handleChange}
                value={values.userEmail}
                onBlur={handleBlur}
                className={errors.userEmail ? "input_cadastro" : "" }
                 /> 
                 {errors.userEmail && touched.userEmail ? <div>{errors.userEmail}</div> : null}

                 <label htmlFor="userPassword">Sua senha</label>
                 <input className={errors.userPassword ? "input_cadastro" : "" }
                id="password"
                type="password" 
                placeholder="Digite sua senha..."
                onChange={handleChange}
                value={values.userPassword} 
                onBlur={handleBlur} />
                {errors.userpassword && touched.userPassword ? <p>{errors.userPassword}</p> : null}
            </div>             

            <div className="enviar-cadastro">
            <div className="link-login">
                 <a href="/cadastro">Esqueci minha senha</a>
                </div>
                <button disabled={isSubmitting}  type="submit">Entrar</button>            
            </div>
        </form>
    </div>     
    )
}

export default Login;
