import {useState} from "react";
import "./Cards.css"

function Cards() {
    const [produtos, setProdutos] = useState([]);

      async function getApi() { 
      const dados = await fetch("https://634b29e533bb42dca41161ad.mockapi.io/login/v1/users");
      const jDados = await dados.json();
      setProdutos(jDados);
      console.log(jDados);

    }
    return (
        <>
        <div className="card">

            <div className="imgBox">
                <img src="./public/image/mouse.png" alt="mouse corsair" className="mouse" />
            </div>

            <div className="contentBox">

            <button onclick={getApi}>Produto</button>
            {produtos.map((elemento) => (
              <><li key={elemento.id}>
                </li><p>tituto: {elemento.titulo}
                </p>

                </>
             ))}
            <h3>Mouse Corsair M65</h3>
                <h2 className="price"> R$ 123.<small>98</small></h2>
                <a href="#" className="buy">Comprar</a>
            </div>
        </div>  



     </>

    )
}

export default Cards;
