import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Cards.css"


function Cards() {
    const [productCards, setProductCards] = useState([]);

    useEffect(() => {
      axios
        .get("URL-DA-API")
        .then(res => {
          //Fazendo o loop para criar os cards
          setProductCards(res.data.map(product => {
            return (
             <div className="card">
              <div className="contentBox">
                <h2>{product.name}</h2>
                <h3>{product.price}</h3>
                <img src={product.image} alt={product.name} />
                <p>{product.description}</p>
                <a href="#" className="buy">Comprar</a>
              </div>
              </div>
            );
          }));
        })
        .catch(err => {
          console.log(err);
        });
    }, []);

    return <div className="product-cards-container">{productCards}</div>;
    };



export default Cards;
